import React, { useEffect, useState } from 'react';
import '../ImageCarousel/ImageCarousel.css';


const images = [
  require('../src/assets/images/img1.jpg'),
  require('../src/assets/images/img2.jpg'),
  require('../src/assets/images/img2.jpg'),
  require('../src/assets/images/img3.jpg'),
  require('../src/assets/images/img4.jpg'),
  require('../src/assets/images/img5.jpg'),
  require('../src/assets/images/img6.jpg'),
];

const ImageCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const slideInterval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex === images.length - 1 ? 0 : prevIndex + 1
      );
    }, 3000); // 3 seconds

    return () => clearInterval(slideInterval); // cleanup on unmount
  }, []);

  return (
    <div className="carousel-container">
      <img src={images[currentIndex]} alt="carousel" className="carousel-image" />
    </div>
  );
};

export default ImageCarousel;
